public class MyViewModel : IViewModel
{
    public int Property { get; set; }
    public ICommand MyCommand { get; }

    MyViewModel()
    {
        MyCommand = new RelayCommand(doSomething, () => Property == 0)
            .RegisterPropertyDependency(this, nameof(Property));
    }
}
